package com.ypwang.medium

class Solution1839 {
    fun longestBeautifulSubstring(word: String): Int {
        var max = 0
        var s = -1
        var level = 0

        val mapping = mapOf(
            'a' to 1,
            'e' to 2,
            'i' to 3,
            'o' to 4,
            'u' to 5
        )

        for ((i, c) in word.withIndex()) {
            if (c in mapping) {
                val l = mapping[c]!!
                if (l !in level..level+1) {
                    if (l == 1) {
                        s = i-1
                        level = 1
                    } else {
                        s = i
                        level = 0
                    }
                }
                else
                    level = l
            }

            if (level == 5)
                max = maxOf(max, i - s)
        }

        return max
    }
}

fun main() {
    println(Solution1839().longestBeautifulSubstring(""))
    println(Solution1839().longestBeautifulSubstring("auoeioueiaaioeuieuoaieuaoeuoaiaoueioiaeuiuaeouaoie"))
    println(Solution1839().longestBeautifulSubstring("aeiaaioaaaaeiiiiouuuooaauuaeiu"))
    println(Solution1839().longestBeautifulSubstring("aeeeiiiioooauuuaeiou"))
    println(Solution1839().longestBeautifulSubstring("a"))
}